import React from "react";
import Dropdown from "./Dropdown"

class Menue extends React.Component {
  constructor() {
    super();
    this.state = {
      matches: {},
      arr:[]
    };
    this.returnFunction=this.returnFunction(this)
  }

  returnFunction(data){
    let match;
      for(let x in data){
        match=x.round
      }
    console.log(match)
    return  (match);

  }
  componentDidMount() {
    fetch(
      "https://raw.githubusercontent.com/openfootball/football.json/master/2018-19/en.1.json"
    )
      .then(response => response.json())
      .then(data =>{
           this.setState({
            matches:data.matches,
             arr: this.returnFunction(data.matches)
          })});


  }


  render() {
    console.log(this.state)
    return(
    <div>
      <Dropdown  props={this.state.matches} />

    </div>
    );
  }
}

export default Menue;
